import React,{useState} from 'react';

const Multipleinput = () => {

    const [userRegistation, setuserResgistration] = useState({ 
         username:'',
         email:'',
         website:'',
         imagelink:'',
         gender:'',
         Course:''

        });

    const[records,setRecord] = useState([]);

    const handleInput = (e) =>{
        const name = e.target.name;
        const value= e.target.value;
        console.log(name,value);

        setuserResgistration({...userRegistation,[name] : value});

    }
    const handleSubmit = (e) =>{
        e.preventDefault();

        const newRecord ={...userRegistation,id:new Date().getTime().toString()}
        console.log(records);
        setRecord([...records,newRecord]);
        console.log(records);

        setuserResgistration({username:'',email:'',website:'',imagelink:'',gender:'',Course:''});

    }
  return <>
    <h1> Student Enrollment Form </h1>
      <form action='' onSubmit={handleSubmit}>
           <div>
              <label htmlFor='username'>Name:</label>
              <input type='text' autoComplete='off' 
              value={userRegistation.username}
              onChange={handleInput}
              name='username' id='usename'/>

          </div>
          <div>
              <label htmlFor='email'>Email:</label>
              <input type='text' autoComplete='off'
              value={userRegistation.email}
              onChange={handleInput}
              name='email' id='email'/>
          </div>
          <div>
              <label htmlFor='website'>Website:</label>
              <input type='text' autoComplete='off'
              value={userRegistation.website}
              onChange={handleInput}
              name='website' id='website'/>
          </div>
          <div>
              <label htmlFor='imagelink'>Image Link:</label>
              <input type='text' autoComplete='off'
              value={userRegistation.imagelink}
              onChange={handleInput}
              name='imagelink' id='imagelink'/>
          </div>
          <div className='Gender' id='gender'>
              <tr>
              <label htmlFor='Gender'>Gender:</label>
              <input type='radio' name='gender'value='Male'/>Male
              <input type='radio' name='gender'value='Female'/> Female
              <input type='radio' name='gender'value='Other'/>Other
              </tr>
          </div>
          <div className='Course'id='Course'>
              <tr>
              <td>
              <select name="Course"></select>
              <label htmlFor='Course'>Course:</label>
              <option value='HTML'>HTML</option>
              <option value='CSS'>CSS</option>
              <option value='JavaScript'>JavaScript</option>
              </td>
              </tr>
          </div>

          <button type='submit'>Enroll Student</button>
          <button type='reset'>Clear</button>
      </form>
      <div>
          {
              records.map((currElem)=>{

              })
          }
      </div>
  </>;
};

export default Multipleinput;
